import React, { Component } from "react"

class AddUser extends Component{
    state = {
        name: "",
        email:""
    }

    updateName = (e) => {
         this.setState({
             name:e.target.value
         })
    }

    updateEmail= (e) => {
         this.setState({
             email: e.target.value
         })    
    }
 

    render() {
        const {newUser}=this.props
        return (
            < div className="card" >
                <div className="card-body">
                    <form onSubmit={e=>e.preventDefault()}>
                        <h5 className="form-header">Form</h5>
                        <label htmlFor="name">Name</label>
                        <input type="text" name="name" id="name" className="form-control" placeholder="name"  onChange={this.updateName.bind(this)}></input>
                        <label htmlFor="job">Job</label>
                        <input type="text" name="job" id="job" className="form-control" placeholder="job"  onChange={this.updateEmail.bind(this)}></input>
                        <button type="submit" className="addUserBtn btn btn-success btn-block" onClick={()=>newUser(this.state)}>Add New User</button>
                    </form>
                </div>
            </div >
    )
    }
}

export default AddUser;
