import React, { Component } from "react"

class AddUser extends Component{
    state = {
        name: "",
        job:""
    }

    updateName = (e) => {
        this.setState({
            name:e.target.value
        })
    }

    updateEmail = (e) => {
        this.setState({
            email: e.target.value
        })
    }

    render() {
        const {updateEmail,updateName}=this.state
        return (
            < div className="card" >
                <div className="card-body">
                    <form>
                        <h5 className="form-header">Form</h5>
                        <label htmlFor="name">Name</label>
                        <input type="text" name="name" id="name" className="form-control" placeholder="name" value={this.state.name} onChange={()=>updateName}></input>
                        <label htmlFor="job">Job</label>
                        <input type="text" name="job" id="job" className="form-control" placeholder="job" value={this.state.job} onChange={()=>updateEmail}></input>
                        <button type="submit" className="btn btn-success btn-block">Add New User</button>
                    </form>
                </div>
            </div >
    )
    }
}

export default AddUser;
