import React,{useState} from "react"
class App extends React.Component{
  constructor() {
    this.state = {
      title: "İlk React Projesi",
      auth:true
  }
}
render(){  
  return (
    <div className="container">
      <h5>{this.state.title}</h5>
      {this.state.auth?<p>Doğrulandı</p>:<p>Doğrulanmadı</p>}
    </div>
  );
}}

export default App;
