import React, { Component } from "react"
import AddUser from "./components/AddUser";
import UserList from "./components/UserList"
class App extends React.Component{
    state = {
      title: "İlk React Projesi",
      auth: true,
      users: [
        {
          id: 1,
          name: "Alex",
          job: "Lawyer"
        }, {
          id: 2,
          name: "Jessica",
          job: "Developer"
        }, {
          id: 3,
          name: "Joseph",
          job: "Engineer"
        }
      ]
    }

  deleteUser = (id) => {
    let filteredUsers = this.state.users.filter(e => e.id !== id)
    this.setState({
      users:filteredUsers
    })
  }

  newUser = (inf) => {
    this.setState({
      users: [...this.state.users,{
                id: this.state.users.length + 1,
                  name: inf.name,
                  job: inf.job
        }]
      })
      console.log(this.state.users)
    }

render(){  
  return (
    <div className="container">
      <h5>{this.state.title}</h5>
      {this.state.auth ? <p>Doğrulandı</p> : <p>Doğrulanmadı</p>}
      <AddUser newUser={this.newUser}></AddUser>
      <UserList users={this.state.users} deleteUser={this.deleteUser}></UserList>
    </div>
  );
}}

export default App;
